parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"answer": {
			"type": "string",
			"description": "The detailed answer or explanation to the student's question"
		},
		"subject": {
			"type": "string",
			"description": "The subject area this question relates to (e.g., Mathematics, Science, etc.)"
		},
		"difficulty_level": {
			"type": "string",
			"description": "Estimated difficulty level: easy, medium, or hard"
		},
		"explanation": {
			"type": "string",
			"description": "Step-by-step explanation suitable for students"
		},
		"key_concepts": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of key concepts covered in this answer"
		},
		"follow_up_suggestions": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "Suggested follow-up questions or topics to explore"
		}
	}
}

parameters.jsCode:
// Get the AI output
const data = $json.output || {};

// Build a comprehensive response
let finalMessage = '';

// Add the main answer
if (data.answer) {
  finalMessage += data.answer + '\n\n';
}

// Add explanation if available
if (data.explanation) {
  finalMessage += '**Explanation:**\n' + data.explanation + '\n\n';
}

// Add follow-up suggestions/questions if available
if (data.follow_up_suggestions && data.follow_up_suggestions.length > 0) {
  finalMessage += '**Practice Questions:**\n';
  data.follow_up_suggestions.forEach((suggestion, index) => {
    finalMessage += `${index + 1}. ${suggestion}\n`;
  });
  finalMessage += '\n';
}

// Add key concepts if available
if (data.key_concepts && data.key_concepts.length > 0) {
  finalMessage += '**Key Concepts:** ' + data.key_concepts.join(', ') + '\n\n';
}

// Add subject and difficulty if available
if (data.subject || data.difficulty_level) {
  finalMessage += '**Subject:** ' + (data.subject || 'N/A') + ' | **Difficulty:** ' + (data.difficulty_level || 'N/A');
}

// Fallback if nothing is available
if (!finalMessage) {
  finalMessage = "I'm here! Ask me anything ðŸ˜Š";
}

// Return it for Respond to Chat node
return [{
  json: {
    finalMessage: finalMessage.trim()
  }
}];
